mapper.select:
select 

from group_coin
where group_id = #{groupId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}

mapper.delete:
delete from group_coin
where group_id = #{groupId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}

mapper.update:
update group_coin


    coin = #{coin,jdbcType=BIGINT},


    get_date = #{getDate,jdbcType=TIMESTAMP},


where group_id = #{groupId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}

update group_coin
set coin = #{coin,jdbcType=BIGINT},
  get_date = #{getDate,jdbcType=TIMESTAMP}
where group_id = #{groupId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}
