mapper.select:
select 

from group_exp
where user_id = #{userId,jdbcType=BIGINT}
  and group_id = #{groupId,jdbcType=BIGINT}

mapper.delete:
delete from group_exp
where user_id = #{userId,jdbcType=BIGINT}
  and group_id = #{groupId,jdbcType=BIGINT}

mapper.update:
update group_exp


    exp = #{exp,jdbcType=BIGINT},


    sign_in_date = #{signInDate,jdbcType=TIMESTAMP},


where user_id = #{userId,jdbcType=BIGINT}
  and group_id = #{groupId,jdbcType=BIGINT}

update group_exp
set exp = #{exp,jdbcType=BIGINT},
  sign_in_date = #{signInDate,jdbcType=TIMESTAMP}
where user_id = #{userId,jdbcType=BIGINT}
  and group_id = #{groupId,jdbcType=BIGINT}
